function [pureSig] = pure_signal(sig, date, c_stD,c_edD, oriAsset, close1, close2, beta, contM1, contM2)
%PURE_SIGNAL 

Ratio = beta;
% 开平仓信号
sigOp = sig(:,1);
sigCl = sig(:,2);
% 开平仓信号所在行
sigLi = zeros(length(sigOp),3); % 方向，开平信号所在行
c = 1;
for t = 1:size(sigLi,1)
    opL = find(sigOp(c:end)~=0,1,'first')+c-1;
    if isempty(opL) || opL==length(sigOp)
        break;
    else
        sigLi(t,1) = sigOp(opL);
        sigLi(t,2) = opL;
    end  
    clL = find(sigCl(opL+1:end)==-sigOp(opL),1,'first')+opL;
    if isempty(clL)
        sigLi(t,3) = size(sigLi,1);
        break;
    else
        sigLi(t,3) = clL;
        c = clL;
    end
end
sigLi(sigLi(:,1)==0,:) = [];
% 开平仓信号根据合约做主力的时间进行调整  
stL = find(date==c_stD,1);
edL = find(date==c_edD,1);
% 开始
stS = find(and(sigLi(:,2)<=stL,sigLi(:,3)>=stL),1,'first'); %这个合约作为主力合约之时的第一个开仓信号，主力之前就开仓，主力后才平仓的情况
if isempty(stS) %第一个合约可能有这个问题，因为是从某个品种的上市日开始的
    stS = find(and(sigLi(:,2)>=stL,sigLi(:,2)<=edL),1,'first'); % 如果没有上述情况，就找第一个才主力后才开仓的信号
    if isempty(stS) %在这个合约作为主力合约期间，没有发出开仓信号
        tdList = [date,zeros(length(sig),8)];
        tdList(:,end) = oriAsset;
        sigAdj = zeros(size(sig));
        pureSig = tdList(:, [1:2 5:6]);
        pureSig = pureSig(pureSig(:, 1) >= c_stD & pureSig(:, 1) <= c_edD, :);
        return;
    end
end

if stS>1
    sigLi(1:stS-1,:) = []; %把该合约还没作为主力合约时候的开仓信号去掉
    sigLi(1,2) = stL-1;  % stL的时候才是主力合约，stL-1的时候还是上一个合约是主力合约呢，当前合约作为主力合约的第一次交易应该从stL开始吧？？？
end
% 结束
edS = find(and(sigLi(:,2)<=edL,sigLi(:,3)>=edL),1,'last'); %这个合约作为主力合约之后的最后一个开仓信号
if isempty(edS) %如果找不到这种信号：在这个时点之前平仓了，但是没有新的开仓信号
    edS = find(sigLi(:,3)<=edL,1,'last'); %在这个时点之前最后一个平仓信号所在行
    sigLi(edS+1:end,:) = [];
else
    sigLi(edS+1:end,:) = [];
    sigLi(end,3) = edL;
end


% 在上述漫雪信号的基础上调整成输入targetPortfolio格式：
tdList = zeros(length(sig),8);
num = size(sigLi,1);
asset = oriAsset;

if num == 0
    pureSig = zeros(size(sig, 1), 3);
    pureSig = [date pureSig];
    return
end

for i = 1:num %逐个信号计算
    opL = sigLi(i,2); %开仓信号所在行                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
    clL = sigLi(i,3); %平仓信号所在行
    sgn1 = sigLi(i,1); %开仓方向-第一个品种的方向
    sgn2 = -sigLi(i,1); %开仓方向-第二个品种的方向
    sgn = sigLi(i,1);
    if clL-opL>1 %不是当根开下根平的情况
        tdList(opL: (clL - 1),1) = sgn;  % 开仓信号当天到平仓信号前一天
%         tdList(opL+1,2) = 2-sgn; %多空开
%         tdList(clL,2) = 3-sgn; %多空平
        % 开仓手数每天再平衡
        chgH1 = 0;
        chgH2 = 0;
        for d = opL:clL-1 
            % 计算开仓手数
            hands = calOpenHands([close1(d) close2(d)],Ratio(d),asset,contM1,contM2); %用前一天的收盘价去计算当天的手数     
            if d==opL %开仓时的手数
                h1 = hands(1);
                h2 = hands(2);
            end
            if d>opL %相比于开仓时手数的变化
                chgH1 = hands(1)-h1;
                chgH2 = hands(2)-h2;
            end
            if abs(chgH1)>2 || abs(chgH2)>2 %两个品种中任意一个的持仓手数变化超过2手，则两个品种一起调仓
                tdList(d,5) = hands(1);
                tdList(d,6) = hands(2);
                h1 = hands(1);
                h2 = hands(2);
            else
                tdList(d,5) = h1;
                tdList(d,6) = h2;
            end
        end
    end
    if clL-opL==1 %当根开下根平
        tdList(opL+1,1) = sgn;
%         tdList(opL+1,2) = 5.5-0.5*sgn;
        hands = calOpenHands([close1(opL) close2(opL)], Ratio(opL), asset, contM1, contM2); %用前一天的收盘价去计算当天的手数
        tdList(opL+1,5) = hands(1);
        tdList(opL+1,6) = hands(2);
       
    end
    
%     % 市值更新
%     asset = tdList(clL,8);
%     % 市值填充
%     if i<num
%         tdList(clL+1:sigLi(i+1,2),8) = asset;
%     else
%         tdList(clL:end,8) = asset;
%     end
        
end

pureSig = tdList(:, [1 5:6]);
pureSig(:, 2) = pureSig(:, 1) .* pureSig(:, 2);
pureSig(:, 3) = -pureSig(:, 1) .* pureSig(:, 3);
pureSig = [date pureSig];
pureSig = pureSig(pureSig(:, 1) >= c_stD & pureSig(:, 1) <= c_edD, :);

end

